import React, { useState, useEffect } from 'react';
import Column from './Components/column';

const App = () => {
  const [tickets, setTickets] = useState([]);
  const [groupBy, setGroupBy] = useState('status'); // Default grouping by status
  const [sortOption, setSortOption] = useState('priority'); // Default sorting by priority
  const [displayOption, setDisplayOption] = useState(''); // State for the display option
  const [selectedOption, setSelectedOption] = useState('');
  const [nestedOption, setNestedOption] = useState('');

  const handleOptionChange = (option) => {
    setSelectedOption(option);
    setNestedOption(''); // Reset nested option when the main option changes
  };

  const handleNestedOptionChange = (option) => {
    setNestedOption(option);
  }
  useEffect(() => {
    // Fetch data from the API
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.quicksell.co/v1/internal/frontend-assignment');
        const data = await response.json();
        console.log('API Data:', data);

        if (Array.isArray(data.tickets)) {
          setTickets(data.tickets);
        } else {
          console.error('Data does not contain a valid tickets array:', data);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleDisplayChange = (option) => {
    // Update the display option
    // setDisplayOption(option);

    // Perform any additional logic based on the selected display option
    // For example, you can update the state or trigger an API request
  };

  return (
    
    <div>
        <h1>Nested Dropdown Example</h1>

      {/* Main Dropdown */}
      <select value={selectedOption} onChange={(e) => handleOptionChange(e.target.value)}>
        <option value="">Select Option</option>
        <option value="option1">Option 1</option>
        <option value="option2">Option 2</option>
        <option value="option3">Option 3</option>
      </select>

      {/* Nested Dropdown */}
      {selectedOption && (
        <div style={{ marginTop: '10px' }}>
          <label>Select Nested Option:</label>
          <select value={nestedOption} onChange={(e) => handleNestedOptionChange(e.target.value)}>
            <option value="">Select Nested Option</option>
            {selectedOption === 'option1' && (
              <>
                <option value="nestedOption1">Nested Option 1</option>
                <option value="nestedOption2">Nested Option 2</option>
              </>
            )}
            {selectedOption === 'option2' && (
              <>
                <option value="nestedOption3">Nested Option 3</option>
                <option value="nestedOption4">Nested Option 4</option>
              </>
            )}
            {/* Add more nested options as needed */}
          </select>
        </div>
      )}

      {/* Display selected options */}
      {selectedOption && <p>Selected Option: {selectedOption}</p>}
      {nestedOption && <p>Selected Nested Option: {nestedOption}</p>}
      <div className=''>
        {/* Display dropdown */}
        <select
          value={displayOption}
          onChange={(e) => handleDisplayChange(e.target.value)}
          className=''
        >
          {/* <option value=''>Select Display</option> */}
          <option value='status'>Group by Status</option>
          {/* <option value='user'>Group by User</option> */}
          <option value='priority'>Group by Priority</option>
          {/* Add more options as needed */}
        </select>
      </div>
      <div className=''>
        {/* Buttons for grouping and sorting options */}
        <button onClick={() => setGroupBy('status')}> Group by Status </button>
        <button onClick={() => setGroupBy('user')}> Group by User </button>
        <button onClick={() => setGroupBy('priority')}> Group by Priority </button>

        <button onClick={() => setSortOption('priority')}> Sort by Priority </button>
        <button onClick={() => setSortOption('title')}> Sort by Title </button>
      </div>

      <Column tickets={tickets} groupBy={groupBy} sortOption={sortOption} />
    </div>
  );
};

export default App;
