// Column.js
import React from 'react';
import Card from './card';

const Column = ({ title, tickets, groupBy, status }) => {
  let filteredTickets;

  if (groupBy === 'status') {
    // If grouping by status, filter based on the specified status
    filteredTickets = tickets.filter((ticket) => ticket.status === status);
  } else {
    // If grouping by user or priority, filter based on the groupBy option
    filteredTickets = tickets.filter((ticket) => ticket[groupBy] === title.toLowerCase());
  }

  return (
    <div className="ticket-column">
      <h2>{groupBy === 'status' ? status : title}</h2>
      <div className="ticket-list">
        {filteredTickets.map((ticket) => (
          <Card key={ticket.id} ticket={ticket} />
        ))}
      </div>
    </div>
  );
};

export default Column;
